import React from "react";
import Title from './ComponentWeather/Title';
import Form from './ComponentWeather/Form';
import moment from 'moment'
import Weathercomp from './ComponentWeather/WeatherComp';

const API_KEY = "cd145d87dcca3f37dda7707b8047db95";

class App extends React.Component {

  state = {
    //Country : undefined,
    city : undefined,
    Datecity : undefined,
    Temperature : undefined,
    description : undefined,
    listdesc :[]
  }

  getWeather = async(e) => {
    const city = e.target.elements.city.value;
    const country = e.target.elements.country.value;
    e.preventDefault();
      //const api_call = await fetch('//api.openweathermap.org/data/2.5/forecast?q=${city},${country}&appid=cd145d87dcca3f37dda7707b8047db95&units=metric');
      const api_call = await fetch(`http://api.openweathermap.org/data/2.5/forecast?q=${city},${country}&appid=${API_KEY}&units=metric`);

      const data= await api_call.json();
      console.log(data);

      const api_call_1 = await fetch(`http://api.openweathermap.org/data/2.5/daily?q=${city},${country}&appid=${API_KEY}&units=metric`);

      const data_1= await api_call_1.json();
      console.log(data_1);

      this.setState({
          country : data_1.city.country,
          city : data_1.city.name,
          //Datecity : data.list[2].dt_txt,
          //Temperature : data.list[2].main.temp,
          //description : data.list[2].weather[0].description,
          listdesc : data.list,

      });


  }


  render(){
    return (
      <div>
        <Title/>
        <Form getWeather={this.getWeather} getcurrentWeather={this.getcurrentWeather}/> 
        <Weathercomp
          Temperature={this.state.Temperature} 
          city={this.state.city}
          country={this.state.country}
          description={this.state.description}
          Datecity ={this.state.Datecity}

        />

        {this.state.listdesc.map(d => {
          if(d.dt_txt.substr(d.dt_txt.length - 8) === "21:00:00"){
            return(
            <div key = {d.dt_txt}>
            {moment(new Date(d.dt_txt)).format("DD MMMM YYYY , dddd")} <span/>
            {Math.round(d.main.temp_min)}&deg;C  - {Math.round(d.main.temp_max)} &deg;C 
            </div>
              )
          }
        }

          )}

      </div>     
      )
  }
}

export default App;
