import React from "react";
import Title from './ComponentWeather/Title';
import Form from './ComponentWeather/Form';
import moment from 'moment'
import Weathercomp from './ComponentWeather/WeatherComp';

const API_KEY = "cd145d87dcca3f37dda7707b8047db95";

class App extends React.Component {

  state = {
    //Country : undefined,
    city : undefined,
    Datecity : undefined,
    Temperature : undefined,
    description : undefined,
    listdesc :[]
  }

  getWeather = async(e) => {
    const city = e.target.elements.city.value;
    const country = e.target.elements.country.value;
    e.preventDefault();
      //const api_call = await fetch('//api.openweathermap.org/data/2.5/forecast?q=${city},${country}&appid=cd145d87dcca3f37dda7707b8047db95&units=metric');
      const api_call = await fetch(`http://api.openweathermap.org/data/2.5/forecast?q=${city},Singapore&appid=${API_KEY}&units=metric`);

      const data= await api_call.json();
      console.log(data);

      this.setState({
          //Country : data.sys.country,
          city : data.name,
          //Datecity : undefined,
          //Temperature : undefined,
          //description : undefined,
          listdesc : data.list,

      });


  }

  render(){
    return (
      <div>
        <Title/>
        <Form getWeather={this.getWeather}/> 
        {this.state.listdesc.map(d => {
          if(d.dt_txt.substr(d.dt_txt.length - 8) === "21:00:00"){
            return(
            <div key = {d.dt_txt}>
            {moment(new Date(d.dt_txt)).format("DD MMMM YYYY , dddd")} 
            <div>{Math.round(d.main.temp_min)}&deg;C  - {d.main.temp_max} &deg;C </div>
            </div>
              )
          }
        }

          )}

      </div>     
      )
  }
}

export default App;
