import React from "react";
import Title from './ComponentWeather/Title';
import Form from './ComponentWeather/Form';
//import Weathercomp from './ComponentWeather/WeatherComp';

//const API_KEY = "cd145d87dcca3f37dda7707b8047db95";

//getWeather={this.getWeather}
class App extends React.Component {

  state = {
    temperature : undefined,
    humidity :undefined,
    listdesc :[]
  }

  getWeather = async(e) => {
    e.preventDefault();
      const api_call = await fetch('//api.openweathermap.org/data/2.5/forecast?q=Singapore,SG&appid=cd145d87dcca3f37dda7707b8047db95');

      const data= await api_call.json();
      console.log(data);

      this.setState({
          temperature : data.city.country,
          listdesc : data.list,
          humidity : data.list[0].main.humidity

      });


  }




  render(){
    return (
      <div>
        <Title/>
        <Form getWeather={this.getWeather}/> 
        <p>{this.state.temperature}</p>
      <div>
        {this.state.listdesc.map(d => {
          if(d.dt_txt.substr(d.dt_txt.length - 8) === "00:00:00"){
            return (<div>{d.dt_txt}</div>)
          }
          return null
        })}
        </div>
      </div>     
      )
  }
}

export default App;
